CREATE TABLE IF NOT EXISTS "user" ("balance" integer NOT NULL DEFAULT (0), "id" varchar PRIMARY KEY NOT NULL, "payoff" integer NOT NULL DEFAULT (0));
CREATE TABLE IF NOT EXISTS "player" ("id" varchar PRIMARY KEY NOT NULL, "image" text NOT NULL, "name" varchar NOT NULL, "shortName" varchar NOT NULL);
CREATE TABLE IF NOT EXISTS "race" ("closeAt" datetime NOT NULL, "id" varchar PRIMARY KEY NOT NULL, "image" varchar NOT NULL, "name" varchar NOT NULL, "startAt" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "odds_item" ("id" varchar PRIMARY KEY NOT NULL, "key" text NOT NULL, "odds" integer NOT NULL, "type" varchar NOT NULL, "raceId" varchar, CONSTRAINT "FK_104a84c60c140e371ccdda7eb71" FOREIGN KEY ("raceId") REFERENCES "race" ("id") ON DELETE NO ACTION ON UPDATE NO ACTION);
CREATE TABLE IF NOT EXISTS "race_entry" ("comment" varchar NOT NULL, "first" integer NOT NULL, "firstRate" float NOT NULL, "id" varchar PRIMARY KEY NOT NULL, "number" integer NOT NULL, "others" integer NOT NULL, "paperWin" integer NOT NULL, "predictionMark" varchar NOT NULL, "rockWin" integer NOT NULL, "scissorsWin" integer NOT NULL, "second" integer NOT NULL, "third" integer NOT NULL, "thirdRate" float NOT NULL, "playerId" varchar, "raceId" varchar, CONSTRAINT "FK_1da0b645d08fb3018bdd82e9747" FOREIGN KEY ("playerId") REFERENCES "player" ("id") ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT "FK_852c5f15c6931109f3cfcb1f4f4" FOREIGN KEY ("raceId") REFERENCES "race" ("id") ON DELETE NO ACTION ON UPDATE NO ACTION);
CREATE TABLE IF NOT EXISTS "betting_ticket" ("createdAt" datetime NOT NULL DEFAULT (datetime('now')), "id" varchar PRIMARY KEY NOT NULL, "key" text NOT NULL, "type" varchar NOT NULL, "raceId" varchar, "userId" varchar, CONSTRAINT "FK_6bacbb25a3b891067166c74e72e" FOREIGN KEY ("raceId") REFERENCES "race" ("id") ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT "FK_f3ea3527c21b06af63f5d2cf2e2" FOREIGN KEY ("userId") REFERENCES "user" ("id") ON DELETE NO ACTION ON UPDATE NO ACTION);
